<template>
  <div class="app-container">
    <el-button @click="onPreview">预览</el-button>
    <el-image-viewer
      v-if="showViewer"
      :on-close="closeViewer"
      :url-list="urls"
    />
  </div>
</template>

<script>
import elImageViewer from 'element-ui/packages/image/src/image-viewer'
export default {
  name: 'PictureShow',
  components: { elImageViewer },
  data() {
    return {
      showViewer: false, // 显示查看器
      urls: ['https://cube.elemecdn.com/6/94/4d3ea53c084bad6931a56d5158a48jpeg.jpeg', 'data:image/jpeg;base64,']
    }
  },
  methods: {
    onPreview() {
      this.showViewer = true
    },
    // 关闭查看器
    closeViewer() {
      this.showViewer = false
    }
  }
}
</script>

